import "../css/allCourses.css";
import { CoursesContext } from "./../context/courses";
// import { CoursesContext } from "../context/courses";
import SingleCourse from "./course";
import { useState } from "react";
const AllCourses = () => {
  //  const { getAllCourses } = useContext(CoursesContext);

  // const [Courses, setCourse] = useState(getAllCourses);
  const [AllCourses, setAllCourses] = useState([
    {
      name: "python",
      course_id: 1,
      subject: "python",
      rating: 100,
      level: "hard",
      img_src:
        "https://banner2.cleanpng.com/20181128/cbr/kisspng-python-programming-basics-for-absolute-beginners-michigan-python-user-group-5-jul-2-18-5bfef921c53528.7857216715434365778078.jpg",
    },
    {
      name: "python",
      course_id: 1,
      subject: "python",
      rating: 100,
      level: "hard",
      img_src:
        "https://i.pinimg.com/736x/d9/7a/d7/d97ad7d1f3f486dc9309a87b0209538a.jpg",
    },
    {
      name: "python",
      course_id: 1,
      subject: "python",
      rating: 100,
      level: "hard",
      img_src:
        "https://www.docker.com/wp-content/uploads/2022/03/vertical-logo-monochromatic.png",
    },
    {
      name: "python",
      course_id: 1,
      subject: "python",
      rating: 100,
      level: "hard",
      img_src:
        "https://banner2.cleanpng.com/20181128/cbr/kisspng-python-programming-basics-for-absolute-beginners-michigan-python-user-group-5-jul-2-18-5bfef921c53528.7857216715434365778078.jpg",
    },
    {
      name: "python",
      course_id: 1,
      subject: "python",
      rating: 100,
      level: "hard",
      img_src:
        "https://www.freepnglogos.com/uploads/javascript-png/javascript-vector-logo-yellow-png-transparent-javascript-vector-12.png",
    },
  ]);

  return (
    <div>
      {AllCourses.map((oneCourse) => {
        return (
          <SingleCourse
            rating={oneCourse.rating}
            name={oneCourse.name}
            subject={oneCourse.subject}
            level={oneCourse.level}
            img_src={oneCourse.img_src}
          />
        );
      })}
    </div>
  );
};

export default AllCourses;
