import "../css/allCourses.css";
import { CoursesContext } from "../context/courses";
import SingleCourse from "./course";
import { useEffect, useContext } from "react";
const AllCourses = () => {
  const { courses, getAllCourses } = useContext(CoursesContext);
  console.log(courses);
  useEffect(() => {
    getAllCourses();
  });
  return (
    <div>
      {courses.map((oneCourse) => {
        return (
          <SingleCourse
            rating={oneCourse.rating}
            name={oneCourse.name}
            subject={oneCourse.subject}
            level={oneCourse.level}
            img_src={oneCourse.img_src}
          />
        );
      })}
    </div>
  );
};

export default AllCourses;
