import React, { createContext, useState } from 'react';
import axios from 'axios';
import jwt from 'jwt-decode'

export const CoursesContext=createContext();

function CoursesProvider (props){
    const {children}=props;
    const [courses,setCourses]=useState([])
    const [errorMsg,setErrorMsg]=useState(null)
    const logOut = async function () {
        localStorage.removeItem('token');
        localStorage.removeItem('name');
    };
    const addNewCourse= async(body)=>{
        const token = localStorage.getItem("token");
        e.preventDefault();

        const {name} = jwt(token)
        const course = await axios.post("http://localhost:4002/api/courses", {
            body: Body,
            name: name,
        },{headers:{
            'x-auth-token':token
        }});
        getAllCourses();
    }
    const getAllCourses= async()=>{
        try {
            const response=await (await axios.get("http://localhost:4000/api/courses")).data
            setCourses(response)
        } catch (error) {
            console.log(error)
        }
    }
    const signup = async function (e, registerInformation) {
        e.preventDefault();

        const {headers} = await axios.post("http://localhost:4002/api/users", {
            name: registerInformation.name,
            email: registerInformation.email,
            password: registerInformation.password,
        });
        localStorage.setItem("token",headers['x-auth-token'])
        localStorage.setItem("name",registerInformation.name);
    };

    const login = async function (e, loginInformation) {
        e.preventDefault();

        const login = await axios.post("http://localhost:4002/api/auth", {
            email: loginInformation.email,
            password: loginInformation.password,
        });
        localStorage.setItem("token", login.data);
    };

    return (
        <CoursesContext.Provider value={{addNewCourse,getAllCourses,login,signup,logOut}}>
            {children}
        </CoursesContext.Provider>
    );
}

export default CoursesProvider;
