import "../css/card.css";
import { useContext, useEffect } from "react";
import { CoursesContext } from "../context/courses";
import Card from "./myCourseCard";
const MyCourses = () => {
  const { myCourses, userCourses } = useContext(CoursesContext);
  useEffect(() => {
    userCourses();
  }, []);
  console.log(myCourses);
  return (
    <div
      style={{
        width: "100%",
        display: "flex",
        justifyContent: "center",
        flexFlow: "column",
        alignItems: "center",
      }}
    >
      {myCourses.map((course) => {
        return <Card rating={course.rating} />;
      })}
    </div>
  );
};

export default MyCourses;
