import "../css/allCourses.css";
const SingleCourse = (props) => {
  const { name, subject, rating, level } = props;
  console.log(props);
  const picSrc = () => {
    const day = "";
    switch (subject) {
      case "python":
        console.log(subject);
        return "https://cdn.pixabay.com/photo/2023/01/14/19/50/flower-7718952_640.jpg";
      case 1:
        day = "Monday";
        break;
    }
    return day;
  };

  return (
    <article className="postcard light blue">
      <a className="postcard__img_link" href="#">
        <img
          className="postcard__img"
          src={
            "https://cdn.pixabay.com/photo/2023/01/14/19/50/flower-7718952_640.jpg"
          }
          alt="Image Title"
        />
      </a>
      <div className="postcard__text t-dark">
        <h1 className="postcard__title blue">
          <a href="#">{subject}</a>
        </h1>
        <div className="postcard__subtitle small">
          <time dateTime="2020-05-25 12:00:00">
            <i className="fas fa-calendar-alt mr-2"></i>Mon, May 25th 2020
          </time>
        </div>
        <div className="postcard__bar"></div>
        <div className="postcard__preview-txt">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi,
          fugiat asperiores inventore beatae accusamus odit minima enim, commodi
          quia, doloribus eius! Ducimus nemo accusantium maiores velit corrupti
          tempora reiciendis molestiae repellat vero. Eveniet ipsam adipisci
          illo iusto quibusdam, sunt neque nulla unde ipsum dolores nobis enim
          quidem excepturi, illum quos!
        </div>
        <ul className="postcard__tagbox">
          <li className="tag__item">
            <i className="fas fa-tag mr-2"></i>
          </li>
          <li className="tag__item">
            <i className="fas fa-clock mr-2"></i>
            {name}
          </li>
          <li className="tag__item play blue">
            <a href="#">
              <i className="fas fa-play mr-2"></i>Play Episode
            </a>
          </li>
        </ul>
      </div>
    </article>
  );
};

export default SingleCourse;
